    def update_prices(self):
        # List of currencies to fetch prices for
        currencies = list(self.currency_labels.keys())

        # Get the current prices for all currencies
        response = cryptocompare.get_price(currencies, currency="USD")

        # Update the corresponding labels based on the currencies
        for currency, label in self.currency_labels.items():
            # Extract the numeric value
            price = response[currency]['USD']

            # Update the label with the new price
            label.setText(str(price))

